d<template>
  <section class="section" id="app">
    <div class="content">
      <div class="columns">
        <!-- loop -->
        <div class="column">
          <!-- <div class="level-left has-text-white">{{ seat.no_of_seats }}</div> -->
          <div class="table-container">
            <table class="table" v-for="(seat, index) in seat" :key="index">
              {{seat.no_of_seats}}
              <!-- Your table content -->
              <td>{{ seat.no_of_seats }}</td>
                            <tr v-if="(index+100)%10 == 0"></tr>
            </table>
          </div>
        </div>
      </div>
    </div>
  </section>
</template>

<script>
import axios from "axios";
export default {
  name: "app",
  data() {
    return {
      seat: [],
      row: 0,
    };
  },
  mounted() {
    this.getSeat(this.$route.params.id);
  },
  methods: {
    getSeat(id) {
      axios
        .get(`http://localhost:3000/movie/${id}`)
        .then((response) => {
          this.seat = response.data;
          console.log(response.data);
        })
        .catch((err) => {
          console.log(err);
        });
    },
    setRow() {
      this.row += 1;
      if (this.row == 10) {
        this.row = 0;
      }
      console.log(this.row);
    },
  },
};
</script>
