export default {
  name: "app",
  data: function data() {
    return {
      seat: [],
      movie: [],
      seatBook: [],
      rows: [],
      cols: [],
      selectSeats: [],
      color: null
    };
  },
  mounted: function mounted() {
    this.getSeat(this.$route.params.id);
  },
  computed: {
    priceOrder: function priceOrder() {
      var total = 0;

      for (var i = 0; i < this.selectSeats.length; i++) {
        for (var j = 0; j < this.seat.length; j++) {
          if (this.seat[j].no_of_seats == this.selectSeats[i]) {
            total += this.seat[j].type_price;
          }
        }
      }

      console.log(total);
      return total;
    }
  },
  methods: {
    getSeat: function getSeat(id) {
      var _this = this;

      axios.get("http://localhost:3000/movie/".concat(id)).then(function (response) {
        _this.seat = response.data.seat;
        _this.movie = response.data.movie[0];

        _this.getSeatBook(response.data.seatBook);

        var date = _this.movie.show_date.split("T")[0];

        date = date.split("-");
        _this.movie.show_date = date[0] + "-" + date[1] + "-" + (parseInt(date[2]) + 1);

        if (_this.seat[0].THEATRE_tid == 1 || _this.seat[0].THEATRE_tid == 2) {
          _this.rows = ["A", "B", "C", "D", "E"];
          _this.cols = ["01", "02", "03", "04", "05", "06", "07", "08", "09", "10"];
        } else if (_this.seat[0].THEATRE_tid == 3) {
          _this.rows = ["A", "B", "C"];
          _this.cols = ["01", "02", "03", "04", "05"];
        }
      }).catch(function (err) {
        console.log(err);
      });
    },
    getSeatBook: function getSeatBook(Book) {
      for (var i = 0; i < Book.length; i++) {
        this.seatBook.push(Book[i].no_of_seats);
      }
    },
    selectSeat: function selectSeat(r, c) {
      if (this.selectSeats.includes(r + c)) {
        this.selectSeats.splice(this.selectSeats.indexOf(r + c), 1);
      } else {
        this.selectSeats.push(r + c);
      }
    },
    getType: function getType(r, c) {
      for (var i = 0; i < this.seat.length; i++) {
        if (this.seat[i].no_of_seats.includes(r + c)) {
          var type = i;
        }
      }

      return this.seat[type].type_name;
    }
  }
};
