export default {
  name: "app",
  data() {
    return {
      seat: [],
      row: 0,
    };
  },
  mounted() {
    this.getSeat(this.$route.params.id);
  },
  methods: {
    getSeat(id) {
      axios
        .get(`http://localhost:3000/movie/${id}`)
        .then((response) => {
          this.seat = response.data;
          console.log(response.data);
        })
        .catch((err) => {
