d<template>
  <section class="section" id="app">
    <div class="content">
      <div class="movie">
        <div class="movie-col-left">
          <a
            href="#/movie/tt0379225"
            class="router-link-exact-active router-link-active"
            ><img
              src="https://images-na.ssl-images-amazon.com/images/M/MV5BMTQxMjMzMTczM15BMl5BanBnXkFtZTcwNzg5MzUyMQ@@._V1_SX300.jpg"
          /></a>
        </div>
        <div class="column level-right">
          <div class="title">
            <p class="title has-text-white">กำลังฉาย</p>
          </div>
          <div class="columns">
            <p class="plot">
              Documentary that looks at the concept of the corporation
              throughout recent history up to its present-day dominance.
            </p>
            <table class="table is-hoverable">
              <tr>
                <td>Released date:</td>
                <td>04 Jun 2004</td>
              </tr>
              <tr>
                <td>Running time:</td>
                <td>145 min</td>
              </tr>
              <tr>
                <td>Director:</td>
                <td>Mark Achbar, Jennifer Abbott</td>
              </tr>
              <tr>
                <td>Cast:</td>
                <td>
                  Mikela Jay, Rob Beckwermert, Christopher Gora, Nina Jones
                </td>
              </tr>
            </table>
          </div>
        </div>
      </div>
      <div class="columns" v-for="seat in seat" :key="seat.seat_id"></div>
    </div>
  </section>
</template>

<script>
import axios from "axios";
export default {
  name: "app",
  data() {
    return {
      seat: [],
      row: 0,
    };
  },
  mounted() {
    this.getSeat(this.$route.params.id);
  },
  methods: {
    getSeat(id) {
      axios
        .get(`http://localhost:3000/movie/${id}`)
        .then((response) => {
          this.seat = response.data.seat;
          console.log(response.data.seat);
        })
        .catch((err) => {
          console.log(err);
        });
    },
  },
};
</script>
