d<template>
 <section class="section" id="app">
      <div class="content">
        <div class="columns is-multiline">
          <!-- loop -->
          <div class="column is-3 mt-5" v-for="seat in seat" :key="seat.seat_id">
            <div class="level-left has-text-white">{{seat.no_of_seats}}</div>
            <div class="level-left has-text-white"></div>
            <div class="card has-background-warning-light">
              <div class="card-image pt-5">
                <figure class="image is-1186x800">
                  <!-- ไว้ใส่รูป -->
                  <img
                    src="https://cdn-icons-png.flaticon.com/512/686/686648.png" 
                    alt="Placeholder image"
                  />
                </figure>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
</template>

<script>
import axios from "axios";
export default {
  name: "app",
  data() {
    return {
      seat: [],
    };
  },
  mounted() {
    this.getSeat(this.$route.params.id);
  },
  methods: {
    getSeat(id) {
      axios
        .get(`http://localhost:3000/movie/${id}`)
        .then((response) => {
          this.seat = response.data;
          console.log(response.data);
        })
        .catch((err) => {
          console.log(err);
        });
    },
  },
};
</script>
