d<template>
  <section class="section" id="app">
    <div class="content">
      <div class="columns">
        <div class="iamge is-6 image is-128x128">
          <img
            src="https://s359.kapook.com/pagebuilder/cf0dd339-b4a5-49ef-8332-25d490e03da8.jpg"
          />
        </div>
        <div class="column is-6 level-right">
          <div class="title">
            <p class="title has-text-white">{{ movie.m_name }}</p>
          </div>
          <hr />
          <div class="detail">
            <p class="has-text-white has-text-justified">
              {{ movie.plot }}
            </p>
            <table
              class="table is-bordered has-background-black has-text-white"
            >
              <tr>
                <td>Date:</td>
                <td>{{ movie.show_date }}</td>
              </tr>
              <tr>
                <td>Time:</td>
                <td>{{ movie.st_time }} - {{ movie.end_time }}</td>
              </tr>
              <tr>
                <td>Director:</td>
                <td>{{ movie.director }}</td>
              </tr>
              <tr>
                <td>Cast:</td>
                <td>
                  {{ movie.actors }}
                </td>
              </tr>
            </table>
          </div>
        </div>
      </div>
      <div class="columns" v-for="seat in seat" :key="seat.seat_id"></div>
    </div>
  </section>
</template>

<script>
import axios from "axios";
export default {
  name: "app",
  data() {
    return {
      seat: [],
      movie: [],
      row: 0,
    };
  },
  mounted() {
    this.getSeat(this.$route.params.id);
  },
  methods: {
    getSeat(id) {
      axios
        .get(`http://localhost:3000/movie/${id}`)
        .then((response) => {
          this.seat = response.data.seat;
          this.movie = response.data.movie[0];
          var date = this.movie.show_date.split("T")[0];
          this.movie.show_date = date;
        })
        .catch((err) => {
          console.log(err);
        });
    },
  },
};
</script>
