<script>
import axios from "axios";
export default {
  name: "app",
  data() {
    return {
        seat: [],
    };
  },
  mounted() {
      this.getSeat(this.$route.params.id);
  },
  methods: {
    getSeat(id) {
      axios
        .get(`http://localhost:3000/movie/${id}`)
        .then((response) => {
          this.movie = response.data;
          for (var i = 0; i < this.movie.length; i++) {
            var date = this.movie[i].show_date.split("T")[0];
            this.movie[i].show_date = date;
          }
          console.log(response.data);
        })
        .catch((err) => {
          console.log(err);
        });
    },
  },
};
</script>
